using System.Text;namespace OOPSample;class Program{    static void Main(string[] args)    {        Console.OutputEncoding = Encoding.UTF8;        var catalog = new List<Book>        {            new Book("Lập trình C# căn bản", "Nguyễn Văn A", "123"),            new Book("OOP in .NET", "John Doe", "456")        };        User u = new User(1, "An");        u.ViewBooks(catalog);        var lib = new Librarian(0, "Thúy");        lib.AddBook(catalog, new Book("Patterns in C#", "Jane Roe", "789"));        lib.ViewBooks(catalog);        // Mượn và trả sách        var book = catalog[0];        book.Borrow();        Console.WriteLine(book);        book.Return();    }}