using System.Text;namespace MyDIProject;public class Program{    public static void Main(string[] args)    {        Console.OutputEncoding = Encoding.UTF8;        var builder = Host.CreateApplicationBuilder(args);        builder.Services.AddHostedService<Worker>();        builder.Services.AddTransient<IMessageWriter, MessageWriter>();        builder.Services.AddTransient<IMessageWriter>(services => new ConsoleMessageWriter(                    services.GetService<INullClass>()        ));        builder.Services.AddTransient<INullClass>(services => new NullClass());        builder.Services.AddSingleton<IServiceCLass, ServiceClass>();        var host = builder.Build();        host.Run();    }}